/*! JSON Studio Lite â€“ FileSaver shim
   Provides global saveAs(blob, filename)
   Simplified for modern browsers only (no IE polyfills). */
(function(){
  if (window.saveAs) return;

  window.saveAs = function(blob, name){
    try {
      const url = URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.style.display = "none";
      a.href = url;
      a.download = name || "download";
      document.body.appendChild(a);
      a.click();
      setTimeout(() => {
        document.body.removeChild(a);
        URL.revokeObjectURL(url);
      }, 200);
    } catch(err){
      console.error("saveAs failed", err);
      alert("Download failed");
    }
  };
})();
